defmodule One do
  def count_occurrences_of(big_str, small_str) do
    String.split(big_str, small_str)
    |> length
    |> - 1
  end

  def calculate(str) do
    count_occurrences_of(str, "(") - count_occurrences_of(str, ")")
  end
end

