defmodule One do
  def list_from_string(str) do
    String.split(str, "", trim: true)
  end

  def direction_from_string("(") do
    1
  end

  def direction_from_string(")") do
    -1
  end

  def character_for_floor(_, _, []) do
    IO.puts "End"
  end

  def character_for_floor(floor, char_pos, [head|tail]) do
    new_floor = floor + direction_from_string(head)
    if new_floor == -1 do
      IO.puts char_pos
    else
      character_for_floor(new_floor, char_pos + 1, tail)
    end
  end

  def calculate(str) do
    data = list_from_string(str)
    character_for_floor(0, 1, data)
  end
end

IO.puts One.calculate(data)
